import React, { useEffect, useRef } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    let animationId;

    try {
      const setCanvasSize = () => {
        canvas.width = canvas.offsetWidth;
        canvas.height = canvas.offsetHeight;
      };
      setCanvasSize();
      window.addEventListener('resize', setCanvasSize);

      let time = 0;
      const girl = {
        x: canvas.width * 0.6,
        y: canvas.height * 0.7,
        size: Math.min(canvas.width, canvas.height) * 0.15,
        hairColor: '#8B4513',
        skinColor: '#FFE4C4',
        dressColor: '#FF69B4'
      };

      const drawGirl = () => {
        ctx.save();

        ctx.fillStyle = girl.hairColor;
        ctx.beginPath();
        ctx.arc(girl.x, girl.y - girl.size * 0.8, girl.size * 0.6, 0, Math.PI * 2);
        ctx.fill();

        ctx.beginPath();
        ctx.arc(girl.x - girl.size * 0.3, girl.y - girl.size * 0.9, girl.size * 0.3, 0, Math.PI * 2);
        ctx.fill();
        ctx.beginPath();
        ctx.arc(girl.x + girl.size * 0.3, girl.y - girl.size * 0.9, girl.size * 0.3, 0, Math.PI * 2);
        ctx.fill();

        ctx.fillStyle = girl.skinColor;
        ctx.beginPath();
        ctx.arc(girl.x, girl.y - girl.size * 0.5, girl.size * 0.4, 0, Math.PI * 2);
        ctx.fill();

        ctx.fillStyle = '#000';
        ctx.beginPath();
        ctx.arc(girl.x - girl.size * 0.15, girl.y - girl.size * 0.6, girl.size * 0.05, 0, Math.PI * 2);
        ctx.fill();
        ctx.beginPath();
        ctx.arc(girl.x + girl.size * 0.15, girl.y - girl.size * 0.6, girl.size * 0.05, 0, Math.PI * 2);
        ctx.fill();

        ctx.strokeStyle = '#000';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(girl.x, girl.y - girl.size * 0.4, girl.size * 0.2, 0, Math.PI);
        ctx.stroke();

        ctx.fillStyle = girl.dressColor;
        ctx.beginPath();
        ctx.ellipse(girl.x, girl.y + girl.size * 0.2, girl.size * 0.4, girl.size * 0.6, 0, 0, Math.PI * 2);
        ctx.fill();

        ctx.fillStyle = girl.skinColor;
        ctx.beginPath();
        ctx.arc(girl.x - girl.size * 0.5, girl.y + girl.size * 0.1, girl.size * 0.15, 0, Math.PI * 2);
        ctx.fill();
        ctx.beginPath();
        ctx.arc(girl.x + girl.size * 0.5, girl.y + girl.size * 0.1, girl.size * 0.15, 0, Math.PI * 2);
        ctx.fill();

        ctx.fillStyle = '#4169E1';
        ctx.beginPath();
        ctx.arc(girl.x - girl.size * 0.2, girl.y + girl.size * 0.8, girl.size * 0.15, 0, Math.PI * 2);
        ctx.fill();
        ctx.beginPath();
        ctx.arc(girl.x + girl.size * 0.2, girl.y + girl.size * 0.8, girl.size * 0.15, 0, Math.PI * 2);
        ctx.fill();

        const bounce = Math.sin(time * 0.05) * 5;
        girl.y = canvas.height * 0.7 + bounce;

        for (let i = 0; i < 20; i++) {
          const x = (Math.sin(time * 0.01 + i) * canvas.width * 0.3) + canvas.width * 0.5;
          const y = (Math.cos(time * 0.01 + i * 0.5) * canvas.height * 0.2) + canvas.height * 0.5;
          const size = Math.sin(time * 0.02 + i) * 3 + 2;

          ctx.fillStyle = `rgba(255, 107, 107, ${0.3 + Math.sin(time * 0.03 + i) * 0.2})`;
          ctx.beginPath();
          ctx.arc(x, y, size, 0, Math.PI * 2);
          ctx.fill();
        }

        ctx.restore();
      };

      const animate = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        drawGirl();
        time++;
        animationId = requestAnimationFrame(animate);
      };

      animate();

      return () => {
        window.removeEventListener('resize', setCanvasSize);
        cancelAnimationFrame(animationId);
      };
    } catch (error) {
      console.error("Error in canvas animation:", error);
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    }
  }, []);

  return (
    <div className="home-section">
      <Container fluid>
        <Row className="align-items-center min-vh-100">
          <Col lg={6} className="hero-content">
            <div className="hero-text animate-slide-in-left">
              <h1 className="hero-title">
                Hi, I'm <span className="highlight">Aditi Garg</span>
              </h1>
              <h2 className="hero-subtitle">
                4th Year B.Tech Student (ECE)
              </h2>
              <p className="hero-description">
                Motivated and dedicated individual eager to apply academic knowledge and practical skills in a professional setting. Proficient in circuit design, programming languages, and communication systems fundamentals with strong problem-solving abilities with a collaborative mindset, coupled with a passion for innovation and learning.
              </p>
              <div className="hero-buttons">
                <Button as={Link} to="/projects" className="btn-custom me-3">
                  View My Work
                </Button>
                <Button as={Link} to="/contact" variant="outline-primary" className="btn-outline-custom">
                  Get In Touch
                </Button>
              </div>
              <div className="social-links">
                <a href="https://github.com/aditigarg1312" target="_blank" rel="noopener noreferrer" className="social-link">
                  <i className="fab fa-github"></i>
                </a>
                <a href="https://www.linkedin.com/in/aditi-garg1312" target="_blank" rel="noopener noreferrer" className="social-link">
                  <i className="fab fa-linkedin"></i>
                </a>
                <a href="mailto:aditigarg1312@gmail.com" className="social-link">
                  <i className="fas fa-envelope"></i>
                </a>
              </div>
            </div>
          </Col>
          <Col lg={6} className="hero-animation">
            <canvas 
              ref={canvasRef} 
              className="animation-canvas"
              style={{ width: '100%', height: '500px' }}
            />
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
